mapper.select:

select min(no)
from post
where category_no=#{categoryNo }


select count(*)
from post
where category_no=#{categoryNo }


select no, title, contents, reg_date as regDate, category_no as categoryNo
from post
where no=#{no }

mapper.update:

update post
set category_no=#{defaultCategoryNo }
where category_no=#{no }
